import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

function ShoppingCart() {
    const product = useSelector((state) => state.shoppingCart.products);
    const total = useSelector((state) => state.shoppingCart.total);

    function handleClick() {
        if ( document.querySelector(".offcanvas").classList.contains("show") && document.querySelector(".offcanvas-backdrop").classList.contains("show") ) 
        {
            document.querySelector(".offcanvas").classList.remove("show");
            document.querySelector(".offcanvas-backdrop").classList.remove("show");
        }
    }

    return (
        <>
            {product.length > 0 ?

                <>
                    {product.map((elem) =>

                        <div className="d-flex align-items-center">
                            <div className="flex-grow-1">
                                <h1>{elem.article}</h1>
                                <p><strong>Quantity:</strong> {elem.qty}</p>
                            </div>
                            <h3>€ {elem.price}</h3>
                        </div>

                    )}

                    <div className="d-flex align-items-center">
                        <h1 className="flex-grow-1">Total: </h1>
                        <h2>€ {total}</h2>
                    </div>
                </>


                :

                'Shopping cart is empty'

            }

            <div className="d-flex justify-content-center mt-5">
                <Link className="btn text-black" to='checkout' onClick={handleClick}>View shopping cart & Checkout</Link>
            </div>
        </>
    )
}

export default ShoppingCart;
