import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import * as formik from 'formik';
import * as yup from 'yup';

function EmailValidator() {
  const { Formik } = formik;

  const schema = yup.object().shape({
    firstName: yup.string().required()
  });

  return (
    <Formik
      validationSchema={schema}
      initialValues={{
        firstName: 'Mark'
      }}
    >
      {({ handleSubmit, handleChange, values, touched, errors }) => (
        <fieldset>
          <Form encType="text/plain" id="newsletterForm" name="newsletterForm"
            autoComplete="on" noValidate onSubmit={handleSubmit}>

            <Form.Group controlId="validationFormik01">

              <div className="form-floating">

                <Form.Control type="email" className="form-control rounded-0 border-0" id="floatingInputNewsletter"
                  name="floatingInputNewsletter" placeholder="name@example.com"
                  pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$" required />
                <Form.Label for="floatingInputNewsletter" id="floatingInputLabel">Email address</Form.Label>

              </div>


              <Form.Control
                type="text"
                name="firstName"
                value={values.firstName}
                onChange={handleChange}
                isValid={touched.firstName && !errors.firstName}
              />
              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
          </Form>
        </fieldset>
      )}
    </Formik>
  );
}

export default EmailValidator;


