import { Formik, Form, useField } from 'formik';
import * as Yup from 'yup';

const MyTextInput = ({ label, ...props }) => {
  // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
  // which we can spread on <input>. We can use field meta to show an error
  // message if the field is invalid and it has been touched (i.e. visited)
  const [field, meta] = useField(props);
  return (
      <>
          <label htmlFor={props.id || props.name}>{label}</label>
          <input className="text-input" {...field} {...props} />
          {meta.touched && meta.error ? (
              <div className="error d-flex align-items-center mt-1">

                  <svg xmlns="http://www.w3.org/2000/svg" width="13" height="13" fill="currentColor" className="bi bi-exclamation-circle me-1" viewBox="0 0 16 16">
                      <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                      <path d="M7.002 11a1 1 0 1 1 2 0 1 1 0 0 1-2 0M7.1 4.995a.905.905 0 1 1 1.8 0l-.35 3.507a.552.552 0 0 1-1.1 0z" />
                  </svg>

                  {meta.error}

              </div>
          ) : null}
      </>
  );
};


const ContactForm = () => {

  const navigate = useNavigate();

  return (
      <>
          <Formik
              initialValues={{
                  firstName: '',
                  email: '',
                  acceptedTerms: false, // added for our checkbox
              }}
              validationSchema={Yup.object({
                  firstName: Yup.string()
                      .min(2, 'Too Short!')
                      .max(15, 'Must be 15 characters or less')
                      .required('Required')
                      .matches(/^[aA-zZ\s]+$/, "Only alphabets are allowed for this field "),
                  email: Yup.string()
                      .email('Invalid email address')
                      .required('Required'),
                  acceptedTerms: Yup.boolean()
                      .required('Required')
                      .oneOf([true], 'You must accept the terms and conditions.'),
              })}
              onSubmit={() => {
                  setTimeout(() => {
                    alert("Thank you! Check your email for a 15% discount");

                  }, 1000);

              }}
          >
              <fieldset>
                  <legend className="fs-5 fw-bold text-center">Leave a message</legend>

                  <Form enctype="text/plain" id="messageForm" name="messageForm" autocomplete="on">

                      <div className="form-floating mt-5 mb-4">
                          <MyTextInput className="form-control rounded-0 border-0 border-bottom"
                              name="firstName"
                              type="text"
                              pattern="^[a-zA-Z]+"
                              required
                              autofocus
                              autocomplete="given-name"
                              aria-required="true"
                              placeholder="Your Name"
                          />
                          <label for="name">Name<sup>*</sup></label>

                      </div>

                      <div className="form-floating mb-4">
                          <MyTextInput className="form-control rounded-0 border-0 border-bottom"
                              name="email"
                              type="email"
                              placeholder="name@example.com"
                              pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$"
                              required
                              autofocus
                              autocomplete="email"
                              aria-required="true"
                          />
                          <label for="surname">Email address<sup>*</sup></label>

                      </div>

                      <div className="form-floating mb-4">
                          <textarea
                              className="form-control rounded-0 h-100 border-0 border-bottom"
                              name="msg"
                              cols="5"
                              rows="2"
                              placeholder="Your Message"
                              required
                              autofocus
                              aria-required="true"
                          ></textarea>
                          <label for="msg">Message<sup>*</sup></label>

                      </div>

                      <MyCheckbox name="acceptedTerms">
                          I accept the terms and conditions
                      </MyCheckbox>

                      <input
                          type="submit"
                          className="btn border rounded-0 w-100 mt-3 mb-3"
                          value="SEND"
                          tabindex="0"
                      />

                  </Form>
              </fieldset>
          </Formik>
      </>
  );
};


const EmailValidator = () => {

  return (

    <Formik
      initialValues={{
        email: "",
      }}

      validationSchema={LoginSchema}

      onSubmit={() => {

        setTimeout(() => {

          alert("Thank you! Check your email for a 15% discount");

        }, 1000);

      }}
    >
      {(props) => (
        <fieldset>
          <Form action='#' encType="text/plain" id="newsletterForm" name="newsletterForm"
            autoComplete="on" noValidate>
            <legend className="fs-6 text-center">Subscribe to our newsletter and receive a surprise</legend>

            <div className="form-floating">

              <Field
                type="email"
                name="email"
                id="floatingInputNewsletter"
                placeholder="name@example.com"
                pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$"
                required
                className={`form-control rounded-0 border-0 ${props
                  .touched
                  .email &&
                  props
                    .errors
                    .email
                  ? "is-invalid"
                  : ""
                  }`}

              />

              <label for="floatingInputNewsletter" id="floatingInputLabel">Email address</label>

              <ErrorMessage
                component="div"
                name="email"
                className="invalid-feedback position-absolute"
              />
            </div>

            <button id="submitButton" type="submit" className="position-relative">
              <img src="/img/arrow-right.svg" alt="Arrow right" />
            </button>

          </Form>

        </fieldset>

      )}
    </Formik>

  );
}

export default EmailValidator;
