import { createSlice } from "@reduxjs/toolkit";

export const shoppingCart = createSlice({
    name: 'shoppingCart',
    initialState: {
        products: [],
        total: 0
    },
    reducers: {
        addToCart: (state, action) => {

            const itemInCart = state.products.find((item) => item.id == action.payload.id);

            if (!itemInCart) {
                state.products.push({
                    ...action.payload,
                    qty: 1
                });
                state.total += action.payload.price
            } else {
                itemInCart.qty++;
                state.total += action.payload.price;
            }
        },

        updateQty: (state, action) => {
            const { id, price, qty } = action.payload;

            if (qty >= 10) {
                alert("You can add max 10 items per order");
                document.getElementById("increase").disable = true;
                return;
            }
            else {

                const itemInCart = state.products.find((item) => item.id == id);
                itemInCart.qty++;
                state.total += price;
            }
        },

        decreaseQty: (state, action) => {
            const { id, price, qty } = action.payload;

            if (qty <= 1) {

                return;

            } else {
                const itemInCart = state.products.find((item) => item.id == id);
                itemInCart.qty--;
                state.total -= price;
            }

        },

        removeItem: (state, action) => {
            const { id, price, qty } = action.payload;
            state.products = state.products.filter((item) => item.id !== id);
            state.total = state.total - (price * qty);

        }
    }
});

export const { addToCart, updateQty, decreaseQty, removeItem } = shoppingCart.actions;
export default shoppingCart.reducer;
