import { Link, useParams, Navigate } from "react-router-dom";
import { useEffect } from 'react';
import { useDispatch } from "react-redux";
import { products } from "../../data/products";
import { addToCart, updateQty, decreaseQty } from '../../features/shoppingCart/shoppingCartSlice'
import './Details.css';

function Details() {

    const dispatch = useDispatch();

    useEffect(() => {
        document.title = `${product[0].article} | SkinNaturals`;
    }, [])

    const { id } = useParams();

    if (!id) {
        return <Navigate to="/" replace />
    };

    let product = products.filter((elem) => elem.id == id);

    function handleClick(elem) {

        dispatch(addToCart(elem));

    };

    function handleIncrement(id, price, qty) {

        dispatch(updateQty({ id: id, price: price, qty: qty }))

    };

    let initialQty=product[0].qty;
    initialQty=1;

    function handleDecrement(id, price, qty) {

        dispatch(decreaseQty({ id: id, price: price, qty: qty }))

    };

    return (
        <>

            <section className="shop m-4">

                <nav aria-label="breadcrumb" role="presentation">
                    <ol className="breadcrumb">

                        <li className="breadcrumb-item">
                            <Link className="link-body-emphasis text-decoration-none text-muted" to="/" tabindex="0"
                                target="_self">
                                Home
                            </Link>
                        </li>

                        <li className="breadcrumb-item">
                            <Link className="link-body-emphasis text-decoration-none text-muted" to="/shop" tabindex="0"
                                target="_self">
                                Shop
                            </Link>
                        </li>

                        <li className="title breadcrumb-item fw-bold" aria-current="page">
                            {product[0].article}
                        </li>

                    </ol>
                </nav>

                <div className="d-md-flex justify-content-center mx-md-4 mx-lg-auto">
                    <div className="productCover d-flex justify-content-center justify-content-md-start my-4">
                        <img id="productPic" src={product[0].img} alt={`SkinNaturals ${product[0].img}`} />
                    </div>

                    <div className="product my-4 mx-md-5">
                        <div className="my-3">
                            <h2 className="title fs-3">{product[0].article}</h2>
                            <p className="qty text-muted"></p>
                            <p className="description mt-3">{product[0].description}</p>
                            <span><strong>Max 10 items per order</strong></span>
                            <h3 className="price fs-2 mt-4">â‚¬ <strong>{product[0].price}</strong></h3>
                        </div>

                        <p><strong>Quantity:</strong></p>

                        <div className="d-flex align-items-center">

                            <div className="d-flex align-items-center">

                                <button id="increase" onClick={() => { handleIncrement(product[0].id, product[0].price, initialQty) }}>+</button>

                                <p className="my-0 mx-2">{initialQty}</p>

                                <button id="decrease" onClick={() => { handleDecrement(product[0].id, product[0].price, initialQty) }}>-</button>

                            </div>

                        </div>

                        <button onClick={() => { handleClick(product[0]) }} type="button" className="btn">Add to shopping cart</button>

                    </div>

                </div>

                <hr className="w-75 mx-auto my-5" />

                <div className="mb-5 mx-md-3 mx-lg-5">

                    <div className="productDescr mb-4">
                        <h3>Description</h3>
                        <p className="description">{product[0].description}</p>
                    </div>

                    <div className="components">
                        <h3 className="fs-4">Components</h3>
                        <p className="ingredient">{product[0].ingredients.map((ingredient) => `${ingredient}, `)}</p>
                    </div>

                    <div className="certifications d-flex justify-content-center align-items-center my-3">
                        <img className="mx-1" src="../../../public/img/vegan.png" alt="Vegan" />
                        <img className="mx-1" src="img/gluten-free.png" alt="Gluten-Free" />
                    </div>

                </div>

            </section>

        </>
    )

};

export default Details;
