import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

function ShoppingCart() {
    const product = useSelector((state) => state.shoppingCart.products);
    const total = useSelector((state) => state.shoppingCart.total);

    function handleClick() {

        document.querySelector("#offcanvasRight").classList.remove("show");
        document.querySelector(".offcanvas-backdrop").classList.remove("show");
    }

    return (
        <>
            {product.length > 0 ?

                <>

                    {product.map((elem) =>

                        <div className="d-flex align-items-center">
                            <div className="flex-grow-1">
                                <img src={elem.img} />
                                <h1 className="fs-4">{elem.article}</h1>
                                <p><strong>Quantity:</strong> {elem.qty}</p>
                            </div>
                            <h2 className="fs-3">€ {elem.price}</h2>
                        </div>

                    )}

                    <div className="position-relative top-50">
                        <div className="d-flex align-items-center">
                            <h1 className="flex-grow-1">Total: </h1>
                            <h2>€ {total}</h2>
                        </div>

                        <div className="d-flex justify-content-center position-relative">
                            <Link className="btn text-black" to='checkout' tabIndex={0} target="_self" onClick={handleClick}>View cart & Checkout</Link>
                        </div>
                    </div>
                </>


                :

                <h5 className="fs-4 text-center m-5">Cart is empty!</h5>

            }
        </>
    )
}

export default ShoppingCart;
