import './Checkout.css';
import { useDispatch, useSelector } from "react-redux";
import { updateQty, decreaseQty, removeItem } from "../../features/shoppingCart/shoppingCartSlice";

function Checkout() {
    const product = useSelector((state) => state.shoppingCart.products);
    const total = useSelector((state) => state.shoppingCart.total);

    const dispatch = useDispatch();

    function handleIncrement(id, price, qty) {

        dispatch(updateQty({ id: id, price: price, qty: qty }))

    };

    function handleDecrement(id, price, qty) {

        dispatch(decreaseQty({ id: id, price: price, qty: qty }))

    };

    function handleRemoval(id, price, qty) {
        dispatch(removeItem({ id: id, price: price, qty: qty }));
    }
    return(
        <>
        <h1>My Shopping Cart</h1>

        <div className="d-flex justify-content-center">

            <div className="p-4 border">
                {product.length > 0 ?

                    <>
                        {product.map((elem) =>

                            <div className="m-4">

                                <div className="d-flex align-items-center">
                                    <h1 className="pe-3">{elem.name}</h1>
                                    <h3 className="m-0">€ {elem.price}</h3>
                                </div>

                                <div>

                                    <p><strong>Quantity:</strong></p>

                                    <div className="d-flex align-items-center">

                                        <div className="d-flex align-items-center">

                                            <button id="increase" onClick={() => { handleIncrement(elem.id, elem.price, elem.qty) }}>+</button>

                                            <p className="my-0 mx-2">{elem.qty}</p>

                                            <button id="decrease" onClick={() => { handleDecrement(elem.id, elem.price, elem.qty) }}>-</button>

                                        </div>

                                    </div>

                                    <button onClick={() => { handleRemoval(elem.id, elem.price, elem.qty) }}>Remove</button>

                                </div>

                            </div>

                        )}
                    </>


                    :

                    'Shopping cart is empty'

                }

            </div>

            <div className="d-flex align-items-center border m-4 p-3">
                <h1 className="flex-grow-1">Total: </h1>
                <h2 className="m-0">€ {total}</h2>
            </div>
        </div>
    </>
    )
};

export default Checkout
