// Render Prop
import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';

const EmailValidator = () => (

  <Formik
    initialValues={{ email: ''}}
    validate={values => {
      const errors = {};
      if (!values.email) {
        errors.email = 'Required';
      } else if (
        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
      ) {
        errors.email = 'Invalid email address';
      }
      return errors;
    }}
    onSubmit={(values, { setSubmitting }) => {
      setTimeout(() => {

      }, 400);
    }}
  >
    {({ isSubmitting }) => (

      <>
      <fieldset>
                        <legend className="fs-6 text-center">Subscribe to our newsletter and receive a surprise</legend>

                        <Form encType="text/plain" id="newsletterForm" name="newsletterForm"
                            autoComplete="on" noValidate >

                            <div className="form-floating">
                                <input type="email" className="form-control rounded-0 border-0" id="floatingInputNewsletter"
                                    name="floatingInputNewsletter" placeholder="name@example.com"
                                    pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$" required />
                                <label for="floatingInputNewsletter" id="floatingInputLabel">Email address</label>

                            </div>
                            <button id="submitButton" type="submit">
                                <img src="/img/arrow-right.svg" alt="Arrow right" />
                            </button>

                        </Form>

                    </fieldset>
      </>

    )}
  </Formik>

);

export default EmailValidator;

<Form>
        <Field type="email" name="email" />
        <ErrorMessage name="email" component="div" />
        <button type="submit" disabled={isSubmitting}>
          Submit
        </button>
      </Form>
