import { Formik, Field, ErrorMessage, Form } from "formik";
import * as Yup from "yup";

const LoginSchema = Yup.object().shape({
  email: Yup.string()
    .email("Invalid email address format")
    .required("Email is required"),
});

const EmailValidator = () => {

  return (

    <Formik
      initialValues={{
        email: "",
      }}

      validationSchema={LoginSchema}

      onSubmit={() => {

        alert("Thank you! Check your email for a 15% discount");

      }}
    >
      {(props) => (
        <fieldset>
          <Form action='#' encType="text/plain" id="newsletterForm" name="newsletterForm"
            autoComplete="on" noValidate>
            <legend className="fs-6 text-center">Subscribe to our newsletter and receive a surprise</legend>

            <div className="form-floating">

              <Field
                type="email"
                name="email"
                id="floatingInputNewsletter"
                placeholder="name@example.com"
                pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$"
                required
                className={`form-control rounded-0 border-0 ${props
                  .touched
                  .email &&
                  props
                    .errors
                    .email
                  ? "is-invalid"
                  : ""
                  }`}

              />

              <label for="floatingInputNewsletter" id="floatingInputLabel">Email address</label>

              <ErrorMessage
                component="div"
                name="email"
                className="invalid-feedback position-absolute"
              />
            </div>

            <button id="submitButton" type="submit" className="position-relative">
              <img src="/img/arrow-right.svg" alt="Arrow right" />
            </button>

          </Form>

        </fieldset>

      )}
    </Formik>

  );
}

export default EmailValidator;
