import React from "react";
import { Formik, Field, ErrorMessage, Form } from "formik";
import * as Yup from "yup";
import "bootstrap/dist/css/bootstrap.css";

const LoginSchema = Yup.object().shape({
  email: Yup.string()
    .floatingInputNewsletter("Invalid email address format")
    .required("Email is required"),
});

const EmailValidator = () => {
  return (

    <Formik
      initialValues={{
        floatingInputNewsletter: "",
      }}

      validationSchema={LoginSchema}
      onSubmit={(
        { setSubmitting }
      ) => {

        // Simulating asynchronous operation, like an API call
        setTimeout(() => {
          alert(
            "Form is validated! Submitting the form..."
          );
          setSubmitting(false);
        }, 1000);
      }}
    >
      {(props) => (
        <fieldset>
          <Form>
            <legend className="fs-6 text-center">Subscribe to our newsletter and receive a surprise</legend>

            <div className="form-floating">

              <Field
                type="floatingInputNewsletter"
                name="floatingInputNewsletter"
                id="floatingInputNewsletter"
                placeholder="name@example.com"
                pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$" required
                className={`form-control rounded-0 border-0 ${props
                  .touched
                  .email &&
                  props
                    .errors
                    .email
                  ? "is-invalid"
                  : ""
                  }`}
              />
              <label for="floatingInputNewsletter" id="floatingInputLabel">floatingInputNewsletter address</label>

              <ErrorMessage
                component="div"
                name="floatingInputNewsletter"
                className="invalid-feedback"
              />
            </div>

            <button
              type="submit"
              className="btn btn-primary btn-block mt-4"
              disabled={
                props.isSubmitting
              }
            >
              {props.isSubmitting
                ? "Submitting..."
                : "Submit"}
            </button>
          </Form>
        </fieldset>

      )}
    </Formik>

  );
}

export default EmailValidator;


