import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';

const [success, setSucces]=useState(false);

const EmailValidator = () => (

  <Formik
    initialValues={{ floatingInputNewsletter: '' }}
    validate={values => {
      const errors = {};
      if (!values.floatingInputNewsletter) {
        errors.floatingInputNewsletter = 'Required';
      } else if (
        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.floatingInputNewsletter)
      ) {
        errors.floatingInputNewsletter = 'Invalid email address';
      }
      return errors;
    }}
    onSubmit={({ setSubmitting }) => {
      setTimeout(() => {


      }, 400);
    }}
  >
    {({ isSubmitting }) => (

      <fieldset>
        <legend className="fs-6 text-center">Subscribe to our newsletter and receive a surprise</legend>

        <Form encType="text/plain" id="newsletterForm" name="newsletterForm"
          autoComplete="on" noValidate >

          <div className="form-floating">

            <Field type="email" className="form-control rounded-0 border-0" id="floatingInputNewsletter"
              name="floatingInputNewsletter" placeholder="name@example.com"
              pattern="[a-z0-9._%+\-]+@[a-z0-9.\-]+\.[a-z]{2,}$" required />
            <label for="floatingInputNewsletter" id="floatingInputLabel">Email address</label>

            <ErrorMessage name="floatingInputNewsletter" component="div" className='mt-2 position-absolute' />

            {success}
          </div>

          <button id="submitButton" type="submit" disabled={isSubmitting}>
            <img src="/img/arrow-right.svg" alt="Arrow right" />
          </button>

        </Form>

      </fieldset>

    )}
  </Formik>

);

export default EmailValidator;
